<?php//Integers$a = array(0,1,2, 3, 4, 5);//Squares$b = array(0,1,4,16,25,36);//Zero, one and four are common in both arrays$expected = array(0,1,4);//array_intersect will return the following form$raw_expected = array(0 => 0,1 => 1,4 => 4);//Find the common subset, raw$raw_common = array_intersect($a,$b);assert($raw_common == $raw_expected);//Refine the common subset$common = array_keys($raw_common);assert($common == $expected);echo "Raw:\n";print_r($raw_expected);echo "Refined:\n";print_r($expected);/* Screen output: Raw:Array(    [0] => 0    [1] => 1    [4] => 4)Refined:Array(    [0] => 0    [1] => 1    [2] => 4)*/?>