<?php$v = array(42,69);var_dump($v);assert( array_key_exists(0,$v));assert( array_key_exists(1,$v));assert(!array_key_exists(2,$v));assert($v);assert( $v[0]);assert( $v[1]);assert(!$v[2]);$w[42] = 69;$w[69] = 99;var_dump($w);assert( array_key_exists(42,$w));assert( array_key_exists(69,$w));assert(!array_key_exists(99,$w));assert($w);assert( $w[42]);assert( $w[69]);assert(!$w[99]);/* Screen outputarray(2) {  [0]=>  int(42)  [1]=>  int(69)}array(2) {  [42]=>  int(69)  [69]=>  int(99)}*/?>