<?php$v=array(1 => 1, 2 => 4, 3 => 9, 4 => 16);echo "Map:\n";print_r($v);echo "Keys:\n";$keys = array_keys($v);print_r($keys);assert($keys == array(1,2,3,4));assert($keys == array_values($keys));echo "Keys of keys:\n";$keys_of_keys = array_keys($keys);print_r($keys_of_keys);assert($keys_of_keys == array(0,1,2,3));assert($keys_of_keys == array_keys($keys_of_keys));assert($keys_of_keys == array_values($keys_of_keys));//assert(1==2);/* Screen output:*/?>