<?php$a = array(0,1,2);$b = array(0,2,4,6);//Concatenate the arrays$expected = array(0,1,2,0,2,4,6);//array_merge will return the following form$raw_expected = array(  0 => 0,  1 => 1,  2 => 2,   3 => 0,  4 => 2,   5 => 4,  6 => 6  );//Find the merged set, raw$raw_common = array_merge($a,$b);assert($raw_common == $raw_expected);//Refine the merged set$common = array_values($raw_common);assert($common == $expected);echo "Raw:\n";print_r($raw_common);echo "Refined:\n";print_r($expected);/* Screen output: Raw:Array(    [0] => 0    [1] => 1    [2] => 2    [3] => 0    [4] => 2    [5] => 4    [6] => 6)Refined:Array(    [0] => 0    [1] => 1    [2] => 2    [3] => 0    [4] => 2    [5] => 4    [6] => 6)*/?>