<?php$v=array(1 => 1, 2 => 4, 3 => 9, 4 => 16);echo "Map:\n";print_r($v);$values = array_values($v);echo "Values:\n";print_r($values);assert($values == array(1,4,9,16));assert($values == array_values($values));/* Screen output:Map:Array(    [1] => 1    [2] => 4    [3] => 9    [4] => 16)Values:Array(    [0] => 1    [1] => 4    [2] => 9    [3] => 16)*/?>