<?php//Let a failed assert terminate the programfunction assert_failure(){    echo 'Too bad, an assertion failed';}function assert_callback( $filename, $line, $message, $description ) {    echo "Assertion failed in file ",      $filename,       " (line ",      $line, ")";    if ($message)    {      echo ", message: '", $message, "'";    }    if ($description)    {      echo ", description: '", $description, "'";    }    echo  "\n";    exit;}assert_options(ASSERT_ACTIVE,   true);assert_options(ASSERT_BAIL,     true);assert_options(ASSERT_WARNING,  true);assert_options(ASSERT_CALLBACK, assert_callback);assert(1==1,'one is equal to one');assert(1==2,'one is equal to two');echo "NEVER GET HERE";/* Screen outputAssertion failed in file MyFolder\PhpAssertExample2.php (line 33), description: 'one is equal to two'*/?>