<?php//From http://www.richelbilderbeek.nl/PhpCopyFile.htmfunction copyFile($from,$to){  assert(file_exists($to));  copy($from,$to);}$from_name = "tmp_from.txt";$to_name   = "tmp_to.txt"  ;if (file_exists($from_name)) { unlink($from_name); }if (file_exists($to_name  )) { unlink($to_name  ); }assert(!file_exists($from_name));assert(!file_exists($to_name  ));$from_file = fopen($from_name,'w');fwrite($from_file,"<h1>Hello world!</h1>");fclose($from_file);assert( file_exists($from_name));assert(!file_exists($to_name  ));copy($from_name,$to_name);assert( file_exists($from_name));assert( file_exists($to_name  ));unlink($from_name);unlink($to_name  );echo "Done";/* Screen outputDone*/?>