<?php//Let a notice terminate the programfunction log_errors($errno, $errstr, $errfile, $errline) {  echo "ERROR "     . "(" . $errno     . ") " . $errstr     . " (" . $errfile     . ", line " . $errline    . ")\n";  exit("log_errors terminated the program");}error_reporting(E_ALL | E_STRICT);set_error_handler("log_errors");//Will generate the following text://Notice: Undefined variable: v in D:\Projects\Test\PhpError_reportingExample1\Php//Error_reportingExample1.php on line 23echo $v[0];/* Screen outputERROR (8) Undefined variable: v (D:\Projects\Test\PhpError_reportingExample1\PhpError_reportingExample1.php, line 23)log_errors terminated the program*/?>