<?php$x = 123;$a = array($x,2,3);$b = array(1,4,9);//array_merge will return the following form$raw_expected = array(  0 => $x,  1 => 2,  2 => 3,   3 => 1,  4 => 4,   5 => 9  );//The first key-value pair its value$expected = $x;//Find the merged set, raw$raw_common = array_merge($a,$b);assert($raw_common == $raw_expected);//Use list on the merged setlist($common) = $raw_common;assert($common == $expected);assert($common == array_values($raw_common)[0]);echo "Raw:\n";print_r($raw_common);echo "Refined:\n";print_r($common);/* Screen output: Raw:Array(    [0] => 123    [1] => 2    [2] => 3    [3] => 1    [4] => 4    [5] => 9)Refined:123*/?>