<?php$my_false=0;echo "Check 7 ways to check if something is not something\n";assert(!$my_false);assert(!!!$my_false);assert(!!!!!$my_false);assert($my_false != 1);assert($my_false <> 1);assert($my_false !== 1);assert(!($my_false == 1));echo "Check if something is still something\n";assert($my_false == 0);/* Screen output:Check 7 ways to check if something is not somethingCheck if something is still something*/?>